const api_urls=["https://r.yotio.jp/yapps-random","https://yapps-random-api.onrender.com"];function wakeup(){fetch_with_fallback("",3000).catch(()=>{alert("サーバに接続できません。時間をおいて再度お試しください。");});}
function fetch_with_timeout(url,options={},timeout=3000){return new Promise((resolve,reject)=>{const controller=new AbortController();const id=setTimeout(()=>controller.abort(),timeout);fetch(url,{...options,signal:controller.signal}).then(response=>{clearTimeout(id);resolve(response);}).catch(err=>{clearTimeout(id);reject(err);});});}
async function fetch_with_fallback(path,timeout=3000){let lastError;for(const base of api_urls){const fullUrl=base+path;try{const response=await fetch_with_timeout(fullUrl,{},timeout);if(response.ok){return response;}else{lastError=new Error(`HTTP ${response.status}`);}}catch(e){lastError=e;}}
throw lastError;}
function update_output(rand_array){const output=document.getElementById('id_output');output.value="";for(i=0;i<rand_array.length;i++){output.value+=rand_array[i]+'\n';}}
function send_and_get(distribution,params){const output=document.getElementById('id_output');output.value="乱数を生成中..";const query=new URLSearchParams(params).toString();const path=`/random/${distribution}?${query}`;fetch_with_fallback(path,3000).then(response=>response.json()).then(data=>{if(data.rand_array){update_output(data.rand_array);}else if(data.error_message){alert(data.error_message);}}).catch(error=>{alert(`乱数生成APIにアクセスできません。\nしばらく待ってから再度お試しください。\n\n${error}`);});}
function uniform(){const trials=document.getElementById('id_trials');const integer_mode=document.getElementById('id_integer_mode');const range=document.getElementsByName('range_uniform');const input_limit_min=document.getElementById('id_input_min_uniform');const input_limit_max=document.getElementById('id_input_max_uniform');const input_digit=document.getElementById('id_input_digit_uniform');const params={};if(integer_mode.checked){params["type"]="int";}
else{params["type"]="float";}
params["trials"]=trials.value;if(range[0].checked){params["max"]=parseInt(input_limit_max.value);params["min"]=parseInt(input_limit_min.value);}
else if(range[1].checked){let digits=parseInt(input_digit.value);let base_num=Math.pow(10,digits-1);let max_num=Math.pow(10,digits)-1;params["max"]=max_num;params["min"]=base_num;}
send_and_get("uniform",params);}
